mapper.delete:
DELETE FROM challenge_comment
 WHERE challenge_board_id = #{challengeBoardId}
   AND challenge_comment_id = #{challengeCommentId}

mapper.update:
UPDATE challenge_comment
   SET content = #{content}
 WHERE challenge_comment_id = #{challengeCommentId}
   AND challenge_board_id = #{challengeBoardId}

mapper.delete:
DELETE 
  FROM proof_comment
 WHERE proof_board_id = #{proofBoardId}
   AND proof_comment_id = #{proofCommentId}

mapper.update:
UPDATE proof_comment
   SET content = #{content}
 WHERE proof_comment_id = #{proofCommentId}
   AND proof_board_id = #{proofBoardId}

mapper.delete:
DELETE 
  FROM fittube_comment
 WHERE video_id = #{videoId}
   AND comment_id = #{commentId}

mapper.update:
UPDATE fittube_comment
   SET content = #{content}
 WHERE comment_id = #{commentId}
   AND video_id = #{videoId}
