mapper.delete:
	DELETE FROM challenge_like
     WHERE board_id = #{boardId}
	   AND writer = #{writer}

mapper.select:
	SELECT count(*)
 	 FROM challenge_like
    WHERE board_id = #{boardId}

	SELECT count(*)
	  FROM challenge_like
     WHERE board_id = #{boardId}
	   AND writer = #{writer}

	SELECT Count(*) 
 	 FROM proof_like
    WHERE proof_board_id = #{boardId}
	  AND writer = #{writer}

mapper.delete:
	DELETE 
	  FROM proof_like
     WHERE proof_board_id = #{boardId}
	   AND writer = #{writer}

	DELETE FROM fittube_like
     WHERE video_id = #{videoId}
	   AND writer = #{writer}

mapper.select:
	SELECT count(*)
 	 FROM fittube_like
    WHERE video_id = #{videoId}

	SELECT count(*)
	  FROM fittube_like
     WHERE video_id = #{videoId}
	   AND writer = #{writer}
